link.script:
        $(document).on('click','.send', function(){
    /* Form Input */
    var input_name = $("#name").val(),
        input_email = $("#email").val(),
        input_phone = $("#phone").val(),
        input_collor = $("#collor").val(),
        input_pembayaran = $("#pembayaran").val(),
        input_alamat = $("#alamat").val();

    /* Whatsapp Setting */
    var walink      = 'https://web.whatsapp.com/send',
        phone       = '6285171602756',
        text        = 'Halo Admin Rizqy.? Saya Mau Order Mini Blender.',
        text2       = 'Terimakasih, Tolong Segera Dikirim Paketnya YA..!',
        text_yes    = 'Orderan Sudah Terkirim.',
        text_no     = 'ISI Form Untuk Melakukan Order.!';

    /* Smartphone Support */
    if (/Android|webOS|iPhone|iPad|iPod|BlackBerry|IEMobile|Opera Mini/i.test(navigator.userAgent)) {
        var walink = 'whatsapp://send';
    }

    if(input_name != "" && input_phone != "" && input_collor != ""){
        /* Whatsapp URL */
        var checkout_whatsapp = walink + '?phone=' + phone + '&text=' + text + '%0A%0A' +
            '*Nama* : ' + input_name + '%0A' +
            '*Email* : ' + input_email + '%0A' +
            '*No. Whatsapp* : ' + input_phone + '%0A' +
            '*Warna Blender* : ' + input_collor + '%0A' +
            '*Metode Pembayaran* : ' + input_pembayaran + '%0A' +
            '*Alamat Lengkap* : ' + input_alamat + '%0A%0A' + text2 + '%0A';

        /* Whatsapp Window Open */
        window.open(checkout_whatsapp,'_blank');
        document.getElementById("text-info").innerHTML = '
'+text_yes+'';
    } else {
        document.getElementById("text-info").innerHTML = '
'+text_no+'';
    } });

body.script:
let slideIndex = 0;
showSlides();

function showSlides() {
  let i;
  let slides = document.getElementsByClassName("mySlides");
  let dots = document.getElementsByClassName("dot");
  for (i = 0; i < slides.length; i++) {
    slides[i].style.display = "none";  
  }
  slideIndex++;
  if (slideIndex > slides.length) {slideIndex = 1}    
  for (i = 0; i < dots.length; i++) {
    dots[i].className = dots[i].className.replace(" active", "");
  }
  slides[slideIndex-1].style.display = "block";  
  dots[slideIndex-1].className += " active";
  setTimeout(showSlides, 4000); // Change image every 2 seconds
}
